package core.cache.def.impl;

/**
 * The type Script args.
 */
public class ScriptArgs {
    /**
     * The Id.
     */
    public int id;
    /**
     * The Args.
     */
    public Object[] args;

    /**
     * Instantiates a new Script args.
     *
     * @param id   the id
     * @param args the args
     */
    public ScriptArgs(int id, Object[] args) {
        this.id = id;
        this.args = args;
    }
}
